import { useState } from "react";
import "./sign_up.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Sign_Up = () => {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [number, setNumber] = useState("");
  const [message, setMessage] = useState("");
  const [categorie, setCategorie] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const userData = {
      username: username,
      password: password,
      email: email,
      number: number,
    };
    try {
      const response = await axios.post("/API/sign-up", userData, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      setMessage(response.data.message);
      setCategorie(response.data.categorie);

      if (response.data.categorie === "success") {
        setTimeout(() => {
          navigate("/login");
        }, 2000);
      }
    } catch (error) {
      console.error("There was an error making the post request", error);
    }
  };

  return (
    <div className="signup-form">
      <div className="signup-icon">&#127973;</div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            placeholder="Username"
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <input
            type="number"
            id="number"
            value={number}
            placeholder="Phone"
            onChange={(e) => setNumber(e.target.value)}
          />
        </div>
        <div>
          <input
            placeholder="Email"
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <input
            placeholder="Password"
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
      {message && <div className={`flash-message ${categorie}`}>{message}</div>}
    </div>
  );
};

export default Sign_Up;
